INSERT INTO `commodity`(`comid`, `comname`, `comkind`, `comstock`, `comdisc`, `comimg`, `comprice`, `comaddtime`, `comstatus`, `comnote`) VALUES (1, '八仙过海闹罗汉', '鲁菜', 55, '原料质地优良，以盐提鲜，以汤壮鲜，调味讲求咸鲜纯正。大葱为山东特产，多数菜肴要要用葱姜蒜来增香提味，炒、熘、爆、扒、烧等方法都要用葱，尤其是葱烧类的菜肴，更是以拥有浓郁的葱香为佳，如葱烧海参、葱烧蹄筋;喂馅、爆锅、凉拌都少不了葱姜蒜。海鲜类量多质优，异腥味较轻，鲜活者讲究原汁原味，虾、蟹、贝、蛤，多用姜醋佐食;燕窝、鱼翅、海参、干鲍、鱼皮、鱼骨等高档原料，质优味寡，必用高汤提鲜', 'https://i.loli.net/2020/05/05/Df3CtO2SNcBrMjz.jpg', 123.00, '2020-05-05 09:41:26', 1, NULL);
INSERT INTO `commodity`(`comid`, `comname`, `comkind`, `comstock`, `comdisc`, `comimg`, `comprice`, `comaddtime`, `comstatus`, `comnote`) VALUES (2, '清蒸加吉鱼', '鲁菜', 40, '鲁菜的突出烹调方法为爆、扒、拔丝，尤其是爆、扒素为世人所称道。爆，分为油爆、盐爆、酱爆、芫爆、葱爆、汤爆、水爆、宫保、爆炒等，充分体现了鲁菜在用火上的功夫。因此，世人称之为“食在中国，火在山东”。', 'https://i.loli.net/2020/05/05/wJlW5jmFq8O6sGz.jpg', 215.00, '2020-05-09 10:33:37', 1, NULL);
INSERT INTO `commodity`(`comid`, `comname`, `comkind`, `comstock`, `comdisc`, `comimg`, `comprice`, `comaddtime`, `comstatus`, `comnote`) VALUES (3, '扒原壳鲍鱼', '鲁菜', 32, '鲁菜以汤为百鲜之源，讲究“清汤”、“奶汤”的调制，清浊分明，取其清鲜。清汤的制法，早在《齐民要术》中已有记载。用“清汤”和“奶汤”制作的菜品繁多，名菜就有“清汤柳叶燕窝”、“清汤全家福”、“氽芙蓉黄管”、“奶汤蒲菜”、“奶汤八宝布袋鸡”、“汤爆双脆”等数十种之多，其中多被列为高档宴席的珍馔美味。', 'https://i.loli.net/2020/05/05/AZY82WotbxQ5XBa.jpg', 200.00, '2020-05-05 09:45:16', 1, NULL);
INSERT INTO `commodity`(`comid`, `comname`, `comkind`, `comstock`, `comdisc`, `comimg`, `comprice`, `comaddtime`, `comstatus`, `comnote`) VALUES (4, '孔府一品锅', '鲁菜', 82, '咸鲜为主，突出本味，擅用葱姜蒜，原汁原味', 'https://i.loli.net/2020/05/05/kiOuYL7M1cjHzWn.jpg', 199.00, '2020-05-05 09:44:38', 1, NULL);
INSERT INTO `commodity`(`comid`, `comname`, `comkind`, `comstock`, `comdisc`, `comimg`, `comprice`, `comaddtime`, `comstatus`, `comnote`) VALUES (5, '花蓝桂鱼', '鲁菜', 11, '孔府菜由家常菜和筵席菜组成，家常菜是府内家人日常饮食的菜肴。\r\n孔府菜菜名很有文化特色。', 'https://i.loli.net/2020/05/05/PXeAqVws4OiamMj.jpg', 166.00, '2020-05-09 10:33:37', 1, NULL);
INSERT INTO `commodity`(`comid`, `comname`, `comkind`, `comstock`, `comdisc`, `comimg`, `comprice`, `comaddtime`, `comstatus`, `comnote`) VALUES (6, '鱼香肉丝', '川菜', 10, '相传灵感来自老菜泡椒肉丝，民国年间由四川籍厨师创制而成，鱼香肉丝这道菜的名称，是抗战时期由蒋介石的厨师最终定名的，并流传至今。\r\n\r\n', 'https://i.loli.net/2020/05/05/QEOTbckAmrFMUWw.png', 79.00, '2020-05-09 10:33:37', 1, NULL);
INSERT INTO `commodity`(`comid`, `comname`, `comkind`, `comstock`, `comdisc`, `comimg`, `comprice`, `comaddtime`, `comstatus`, `comnote`) VALUES (7, '宫保鸡丁', '川菜', 50, '是一道闻名中外的特色传统名菜，该菜式的起源与鲁菜中的酱爆鸡丁，和贵州菜的胡辣子鸡丁有关，后被清朝山东巡抚、四川总督丁宝桢改良发扬，形成了一道新菜式——宫保鸡丁，并流传至今，此道菜也被归纳为北京宫廷菜。\r\n\r\nl', 'https://i.loli.net/2020/05/05/AjwvkOYUGHsulrM.jpg', 89.00, '2020-05-11 09:39:28', 1, NULL);
INSERT INTO `commodity`(`comid`, `comname`, `comkind`, `comstock`, `comdisc`, `comimg`, `comprice`, `comaddtime`, `comstatus`, `comnote`) VALUES (8, '水煮鱼', '川菜', 221, '也称为江水煮江鱼、水煮鱼片，最早流行于重庆市渝北区翠云乡。水煮鱼通常由新鲜草鱼、豆芽、辣椒等食材制作而成。“油而不腻、辣而不燥、麻而不苦、肉质滑嫩”是其特色。\r\n\r\n', 'https://i.loli.net/2020/05/05/SnvrxpVcmUsBEdy.jpg', 99.00, '2020-05-11 09:39:28', 1, NULL);
INSERT INTO `commodity`(`comid`, `comname`, `comkind`, `comstock`, `comdisc`, `comimg`, `comprice`, `comaddtime`, `comstatus`, `comnote`) VALUES (9, '夫妻肺片', '川菜', 320, '由郭朝华、张田政夫妻创制而成。通常以牛头皮、牛心、牛舌、牛肚、牛肉为主料，进行卤制，而后切片。再配以辣椒油、花椒面等辅料制成红油浇在上面。\r\n\r\n', 'https://i.loli.net/2020/05/05/Q3c6hOCj25IFeyR.jpg', 35.00, '2020-05-11 09:39:28', 1, NULL);
INSERT INTO `commodity`(`comid`, `comname`, `comkind`, `comstock`, `comdisc`, `comimg`, `comprice`, `comaddtime`, `comstatus`, `comnote`) VALUES (10, '烧乳猪', '粤菜', 12, '早在西周时代已列为“八珍”之一，那时称为“炮豚”，即烤乳猪。在《齐民要术》一书中也记有烤乳猪的制作方法，并说它“色同琥珀，又类真金，入口则消，壮若凌雪，含浆膏润，特异凡常也”。清康熙时，曾为宫廷名菜，成为“满汉全席”中的一道主要菜肴。直到民国初期山东还经营此菜。后在广州和上海盛行，成为最著名的广东名菜。现今“烧乳猪”为广州和港澳地区许多著名菜馆的首席名菜，深受中外顾客欢迎。', 'https://i.loli.net/2020/05/05/tJQl8qc6onI4ZNx.jpg', 299.00, '2020-05-05 09:47:11', 1, NULL);
INSERT INTO `commodity`(`comid`, `comname`, `comkind`, `comstock`, `comdisc`, `comimg`, `comprice`, `comaddtime`, `comstatus`, `comnote`) VALUES (11, '阿一鲍鱼', '粤菜', 32, '中国人素来讲究吃，千年的文明史中就包括食文化。在中式宴席里，鲍鱼、鱼翅、海参、燕窝是上品，这四大名品早已在历史名厨的手术演绎出一整套烹饪方法。而香港富临集团阿一鲍鱼酒家却在传统烹任的基础上百尺竿头，创造了独特的海鲜烹饪方法，尤其是特制的阿一鲍鱼这道菜，堪称一绝，被国际美食界公认为中国菜之极品，从而享有极高的声誉。 阿一鲍鱼被首届中国粤菜峰会评为粤菜十大名菜之一。', 'https://i.loli.net/2020/05/05/Vb54Y9odFELc1vD.jpg', 222.00, '2020-05-05 09:47:31', 1, NULL);
INSERT INTO `commodity`(`comid`, `comname`, `comkind`, `comstock`, `comdisc`, `comimg`, `comprice`, `comaddtime`, `comstatus`, `comnote`) VALUES (12, '广东文昌鸡', '粤菜', 22, '广东文昌鸡是广东省传统的汉族名菜，属于粤菜系。以海南岛文昌鸡为主料，配以火腿 、鸡肝、郊菜，经煮、蒸、炒而成。此菜造型美观，芡汁明亮，肉质滑嫩，香味甚浓，肥而不腻。三样配料颜色不同，滋味各异，为广州八大名鸡之一。', 'https://i.loli.net/2020/05/05/mdslkPaVxEY64qR.jpg', 331.00, '2020-05-05 09:47:49', 1, NULL);
INSERT INTO `commodity`(`comid`, `comname`, `comkind`, `comstock`, `comdisc`, `comimg`, `comprice`, `comaddtime`, `comstatus`, `comnote`) VALUES (13, '生拆蟹肉烩海虎翅', '粤菜', 15, '鱼翅是高档粤菜的重要食材。红烧大群翅原是大三元的传统招牌菜，据说在上世纪二三十年代，要花上60银元才能吃得上这道菜。 选用老虎鲨等大鲨鱼的鱼翅作为原料，经浸发、滚煨翅等多道工序而成，出品翅针透明软化，味道鲜美、韧中带脆、浓而不腻。', 'https://i.loli.net/2020/05/05/m5vQdDARFf6c7Vb.jpg', 399.00, '2020-05-05 09:48:19', 1, NULL);
INSERT INTO `commodity`(`comid`, `comname`, `comkind`, `comstock`, `comdisc`, `comimg`, `comprice`, `comaddtime`, `comstatus`, `comnote`) VALUES (14, '蜜汁叉烧', '粤菜', 62, '蜜汁叉烧是广东省传统名菜之一，属于粤菜菜系。\"叉烧\"是从\"插烧\"发展而来的。主要以猪肉(瘦)、叉烧酱、蚝油、蒜、生抽、料酒、蜂蜜、白糖、花生油为原材料制作而成。', 'https://i.loli.net/2020/05/05/oMsYbOc46BIAJgW.jpg', 199.00, '2020-05-05 09:49:49', 1, NULL);
